package student;import javafx.scene.control.TextArea;import lombok.Getter;import lombok.Setter;import searching.ConverterCsvToObjects;import searching.FileOpening;import javafx.collections.FXCollections;import javafx.collections.ObservableList;import javafx.fxml.FXML;import javafx.fxml.Initializable;import javafx.scene.control.ComboBox;import javafx.scene.layout.GridPane;import searching.Lesson;import java.net.URL;import java.util.ArrayList;import java.util.List;import java.util.ResourceBundle;@Getter@Setterpublic class StudentController implements Initializable {    @FXML    private ComboBox<String> listOfGroupsComboBox;    @FXML    private GridPane schedule;    private ArrayList<String> daysOfTheWeek = new ArrayList<String>() {{        add("Poniedziałek");        add("Wtorek");        add("Sroda");        add("Czwartek");        add("Piątek");    }};    private ArrayList<String> hoursInTheDay = new ArrayList<String>() {{        add("8.00");        add("9.00");        add("10.00");        add("11.00");        add("12.00");        add("13.00");        add("14.00");        add("15.00");        add("16.00");        add("17.00");        add("18.00");        add("19.00");        add("20.00");    }};    private ObservableList<String> listOfGroups = FXCollections.observableArrayList();    public void initialize(URL location, ResourceBundle resources) {        listOfGroupsComboBox.setItems(listOfGroups);        insertButtonsTest();    }    public void insertButtonsTest() {        FileOpening fileOpening = new FileOpening();        List<Lesson> listOfLessons;        fileOpening.openFile();        listOfLessons = ConverterCsvToObjects.finalListOfLessons;        for(int i = 0; i<listOfLessons.size(); i++){            addToListOfGroups(listOfLessons.get(i).getGroups().get(0));        }        printNameOfDay();        printHourOfDay();    }    private void printNameOfDay() {        int j = 0;        for (int i = 1; i < 6; i++) {            TextArea textarea = new TextArea();            textarea.setText(daysOfTheWeek.get(i-1));            textarea.setStyle("-fx-opacity: 1;");            textarea.setId("Label"+daysOfTheWeek.get(i-1));            textarea.setDisable(true);            schedule.add(textarea, i, j);        }    }    private void printHourOfDay(){        int i = 0;        for (int j = 1; j < 13; j++) {            TextArea textarea = new TextArea();            textarea.setText(hoursInTheDay.get(j-1));            textarea.setStyle("-fx-opacity: 1;");            textarea.setId("Label"+hoursInTheDay.get(j-1));            textarea.setDisable(true);            schedule.add(textarea,i,j);        }    }    private void addToListOfGroups(String group){        ObservableList<String> temporary = FXCollections.observableArrayList();        temporary = getListOfGroups();        temporary.add(group);        setListOfGroups(temporary);    }}